' . $parametros . '){ ';    }    /**     * JavaScript::fechaFuncao()     *      * @return     */    public function fechaFuncao()    {        return ' } ';    }    /**     * JavaScript::entreJS()     *      * @param mixed $codigo     * @return     */    public function entreJS($codigo)    {        return $this->abreJS() . $codigo . $this->fechaJS();    }    /**     * JavaScript::redireciona()     *      * @param mixed $url     * @return     */    public function redireciona($url)    {        return ' window.location="' . $url . '" ';    }    /**     * JavaScript::abreLoadJQuery()     *      * @return     */    public function abreLoadJQuery()    {        return ' $(document).ready(function() { ';    }    /**     * JavaScript::fechaLoadJQuery()     *      * @return     */    public function fechaLoadJQuery()    {        return ' }); ';    }}